contracts/badge/CertificationNFT.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../base/ERC721Upgradeable.sol";
import "../base/Linkable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title Certification NFT for Blockchain Academy
 * @author Eric Lee
 *
 * @notice This contract is used to mint NFTs for Blockchain Academy Certification.
 *
 *         Certification is the top level NFT for Blockchain Academy.
 *         It is minted for those who:
 *         - Complete all BuidlCamp courses and exercises
 *         - Finish certain courses combination & pass certain tests
 *         - To be extended...
 *
 *         Certification NFT is a ERC721 token.
 *
 */
contract CertificationNFT is OwnableUpgradeable, ERC721Upgradeable, Linkable {
    using Strings for uint256;

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Variables **************************************** //
    // ---------------------------------------------------------------------------------------- //

    uint256 counter;

    string public baseURI;

    // Token id => Certification id
    mapping(uint256 => uint256) certificationIds;

    // User address => certification id => token id
    mapping(address => mapping(uint256 => uint256)) public userMinted;

    // ---------------------------------------------------------------------------------------- //
    // *************************************** Events ***************************************** //
    // ---------------------------------------------------------------------------------------- //

    event BaseURIChanged(string oldURI, string newURI);

    event CertificationNFTMinted(
        address indexed to,
        uint256 indexed tokenId,
        uint256 indexed certificationId
    );

    event CertificaitonNFTBurned(
        uint256 indexed tokenId,
        uint256 indexed certificationId
    );

    // ---------------------------------------------------------------------------------------- //
    // ************************************* Constructor ************************************** //
    // ---------------------------------------------------------------------------------------- //

    function initialize(string memory _name, string memory _symbol)
        public
        initializer
    {
        __Ownable_init();
        __ERC721_init(_name, _symbol);
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************ Set Functions ************************************* //
    // ---------------------------------------------------------------------------------------- //

    function setBaseURI(string memory _uri) public onlyOwner {
        emit BaseURIChanged(_uri, baseURI);
        baseURI = _uri;
    }

    // ---------------------------------------------------------------------------------------- //
    // ************************************ Main Functions ************************************ //
    // ---------------------------------------------------------------------------------------- //

    function mint(address _to, uint256 _certificationId)
        external
        onlyOwner
        returns (uint256)
    {
        require(
            userMinted[_to][_certificationId] == 0,
            "CertificationNFT: already minted"
        );

        uint256 tokenId = counter++;

        _safeMint(_to, tokenId);

        certificationIds[tokenId] = _certificationId;

        userMinted[_to][_certificationId] = tokenId;

        emit CertificationNFTMinted(_to, tokenId, _certificationId);

        return tokenId;
    }

    function burn(uint256 _tokenId) external {
        require(
            ownerOf[_tokenId] == msg.sender,
            "Only the token owner can burn"
        );

        _burn(_tokenId);

        uint256 certificationId = certificationIds[_tokenId];
        delete userMinted[msg.sender][certificationId];

        emit CertificaitonNFTBurned(_tokenId, certificationId);
    }

    function tokenURI(uint256 _tokenId) public view returns (string memory) {
        return _tokenURI(_tokenId);
    }

    // ---------------------------------------------------------------------------------------- //
    // *********************************** Internal Functions ********************************* //
    // ---------------------------------------------------------------------------------------- //

    /**
     * @notice Generate the token URI
     *
     *         Token URI will be stored on backend
     *         URI = "https://api.blockchainacademy.org/api/v1/nft/course/{courseId}"
     */
    function _tokenURI(uint256 _tokenId) internal view returns (string memory) {
        return string.concat(baseURI, certificationIds[_tokenId].toString());
    }
}

contracts/base/ERC721Upgradeable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * @title ERC721 Token Contract
 *
 * @notice This contract is mainly based on solmate/ERC721.sol
 *         By default, not implement IERC721Metadata
 */
abstract contract ERC721Upgradeable is Initializable {
    address internal constant ZERO_ADDRESS = address(0);

    string public name;
    string public symbol;

    mapping(uint256 => address) public ownerOf;
    mapping(address => uint256) public balanceOf;

    mapping(uint256 => address) public getApproved;
    mapping(address => mapping(address => bool)) public isApprovedForAll;

    /**
     * @dev Emitted when `tokenId` token is transferred from `from` to `to`.
     */
    event Transfer(
        address indexed from,
        address indexed to,
        uint256 indexed tokenId
    );

    /**
     * @dev Emitted when `owner` enables `approved` to manage the `tokenId` token.
     */
    event Approval(
        address indexed owner,
        address indexed approved,
        uint256 indexed tokenId
    );

    /**
     * @dev Emitted when `owner` enables or disables (`approved`) `operator` to manage all of its assets.
     */
    event ApprovalForAll(
        address indexed owner,
        address indexed operator,
        bool approved
    );

    function intialize(string memory _name, string memory _symbol)
        public
        initializer
    {
        __ERC721_init(_name, _symbol);
    }

    function __ERC721_init(string memory _name, string memory _symbol)
        internal
        onlyInitializing
    {
        name = _name;
        symbol = _symbol;
    }

    function approve(address _spender, uint256 _tokenId) public virtual {
        address owner = ownerOf[_tokenId];

        require(
            msg.sender == owner || isApprovedForAll[owner][msg.sender],
            "NOT_AUTHORIZED"
        );

        getApproved[_tokenId] = _spender;

        emit Approval(owner, _spender, _tokenId);
    }

    function setApprovalForAll(address _operator, bool _approved)
        public
        virtual
    {
        isApprovedForAll[msg.sender][_operator] = _approved;

        emit ApprovalForAll(msg.sender, _operator, _approved);
    }

    function transferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual {
        _beforeTokenTransfer(from, to, id);

        require(from == ownerOf[id], "WRONG_FROM");

        require(to != ZERO_ADDRESS, "INVALID_RECIPIENT");

        require(
            msg.sender == from ||
                isApprovedForAll[from][msg.sender] ||
                msg.sender == getApproved[id],
            "NOT_AUTHORIZED"
        );

        // Underflow of the sender's balance is impossible because we check for
        // ownership above and the recipient's balance can't realistically overflow.
        unchecked {
            --balanceOf[from];

            ++balanceOf[to];
        }

        ownerOf[id] = to;

        delete getApproved[id];

        emit Transfer(from, to, id);

        _afterTokenTransfer(from, to, id);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id
    ) public virtual {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                IERC721Receiver(to).onERC721Received(
                    msg.sender,
                    from,
                    id,
                    ""
                ) ==
                IERC721Receiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        bytes calldata data
    ) public virtual {
        transferFrom(from, to, id);

        require(
            to.code.length == 0 ||
                IERC721Receiver(to).onERC721Received(
                    msg.sender,
                    from,
                    id,
                    data
                ) ==
                IERC721Receiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        returns (bool)
    {
        return
            interfaceId == 0x01ffc9a7 || // ERC165 Interface ID for ERC165
            interfaceId == 0x80ac58cd || // ERC165 Interface ID for ERC721
            interfaceId == 0x5b5e139f; // ERC165 Interface ID for ERC721Metadata
    }

    function _mint(address to, uint256 id) internal virtual {
        require(to != ZERO_ADDRESS, "INVALID_RECIPIENT");

        require(ownerOf[id] == ZERO_ADDRESS, "ALREADY_MINTED");

        // Counter overflow is incredibly unrealistic.
        unchecked {
            ++balanceOf[to];
        }

        ownerOf[id] = to;

        emit Transfer(ZERO_ADDRESS, to, id);
    }

    function _burn(uint256 id) internal virtual {
        address owner = ownerOf[id];

        require(owner != ZERO_ADDRESS, "NOT_MINTED");

        // Ownership check above ensures no underflow.
        unchecked {
            --balanceOf[owner];
        }

        delete ownerOf[id];

        delete getApproved[id];

        emit Transfer(owner, ZERO_ADDRESS, id);
    }

    function _safeMint(address to, uint256 id) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                IERC721Receiver(to).onERC721Received(
                    msg.sender,
                    address(0),
                    id,
                    ""
                ) ==
                IERC721Receiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function _safeMint(
        address to,
        uint256 id,
        bytes memory data
    ) internal virtual {
        _mint(to, id);

        require(
            to.code.length == 0 ||
                IERC721Receiver(to).onERC721Received(
                    msg.sender,
                    address(0),
                    id,
                    data
                ) ==
                IERC721Receiver.onERC721Received.selector,
            "UNSAFE_RECIPIENT"
        );
    }

    function _beforeTokenTransfer(
        address _from,
        address _to,
        uint256 _tokenId
    ) internal virtual {}

    function _afterTokenTransfer(
        address _from,
        address _to,
        uint256 _tokenId
    ) internal virtual {}
}

contracts/base/Linkable.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.17;

import "./ILinkableSBT.sol";

abstract contract Linkable {
    // Token id => Keccak256(contract, id)
    mapping(uint256 => bytes32) public links;

    /**
     * @notice Ensure that this tokenId not already linked
     */
    modifier notLinked(uint256 _tokenId) {
        require(
            links[_tokenId] == bytes32(0),
            "Linkable: token already linked to another sbt"
        );
        _;
    }

    /**
     * @notice Link a token to a SoulBoundToken
     *
     *         The target token address must be a soul-bound token
     *         It should check the target has the interface
     */
    function _link(
        address _sbt,
        uint256 _targetTokenId,
        uint256 _tokenId
    ) internal virtual notLinked(_tokenId) {
        links[_tokenId] = keccak256(abi.encodePacked(_sbt, _targetTokenId));

        require(
            _sbt.code.length != 0 &&
                ILinkableSBT(_sbt).onERC721Linked(
                    address(this),
                    _tokenId,
                    _sbt,
                    _targetTokenId
                ) ==
                ILinkableSBT.onERC721Linked.selector,
            "UNSAFE_LINK"
        );
    }
}

